import rclpy
from rclpy.node import Node 

class PresonNode(Node):
    def __init__(self,node_name:str,name_VALUE: str,age_VALUE: int) -> None:
        super().__init__(node_name)
        self.name = name_VALUE
        self.age = age_VALUE
        print('PresonNode __init__ 方法被调用了')
    
    def eat(self,foodname: str):
        #print(f"{self.name},{self.age}岁,爱吃{foodname}")   
        self.get_logger().info(f"{self.name},{self.age}岁,爱吃{foodname}")

def main():
    rclpy.init()
    node= PresonNode('birdjiedian','bigbird',18)
    node.eat('6')
    rclpy.spin(node)
    rclpy.shutdown()